function handleBaseSections(entries){entries.forEach(entry=>{if(entry.isIntersecting&&entry.target.classList.contains('animated')==!1){if(entry.target.classList.contains('card')){if(window.innerWidth<768){entry.target.classList.add('card-rotation-enter')}else if(window.innerWidth>=768){entry.target.classList.add('scale-up-center')}}else if(entry.target.id=='CEO-and-founder-img'){if(window.innerWidth<768){entry.target.classList.add('flip-in-diag-1-tr')}else if(window.innerWidth>=768){entry.target.classList.add('scale-up-ver-center')}}else if(entry.target.id=='CEO-and-founder-subtitle'){entry.target.classList.add('tracking-in-expand')}else if(entry.target.id=='CEO-and-founder-description-p1'||entry.target.id=='CEO-and-founder-description-p2'){entry.target.classList.add('text-focus-in')}
if(entry.target.classList.contains('description-container')){entry.target.classList.add('appear-diag-enter')}}})}
let baseSectionOptions={root:null,rootMargin:'0px',threshold:0.2}
let baseSectionObserver=new IntersectionObserver(handleBaseSections,baseSectionOptions)
let cards=document.querySelectorAll('div.card')
let CAFImg=document.querySelector('img#CEO-and-founder-img')
let CAFSubtitle=document.querySelector('h2#CEO-and-founder-subtitle')
let CAFDescription1=document.querySelector('p#CEO-and-founder-description-p1')
let CAFDescription2=document.querySelector('p#CEO-and-founder-description-p2')
let descriptionContainer=document.querySelector('div.description-container')
let baseTargets=[CAFImg,CAFSubtitle,CAFDescription1,CAFDescription2,descriptionContainer]
cards.forEach(card=>{baseTargets.push(card)})
baseTargets.forEach(target=>{baseSectionObserver.observe(target)})