let menuOverlay=document.querySelector('div#menu-overlay')
let mainArea=document.querySelector('main')
document.querySelector('button#open-menu-btn').addEventListener('click',function(){menuOverlay.classList.replace('closed','opened')
mainArea.style.opacity='0'
document.body.style.overflowY='hidden'})
document.querySelector('button#close-menu-btn').addEventListener('click',function(){menuOverlay.classList.replace('opened','closed')
mainArea.style.opacity='1'
document.body.style.overflowY='auto'})
let supportBtn=document.querySelector('div#support-team')
window.onscroll=function(){if(window.scrollY>=900&&supportBtn.classList.contains('d-none-i')==!1){supportBtn.classList.replace('d-none','d-flex')}else{supportBtn.classList.replace('d-flex','d-none')}}
